import { Component } from '@angular/core';
import { AlertController, IonicPage, ModalController, NavParams, PopoverController, ViewController } from 'ionic-angular';
import { ServicioOptions } from '../../interfaces/servicio-options';
import { ClienteOptions } from '../../interfaces/cliente-options';
import { ReservaOptions } from '../../interfaces/reserva-options';
import moment from 'moment';
import { CarritoOptions } from '../../interfaces/carrito-options';

/**
 * Generated class for the ReservaPage page.
 *
 * See https://ionicframework.com/docs/components/#navigation for more info on
 * Ionic pages and navigation.
 */

@IonicPage()
@Component({
  selector: 'page-reserva',
  templateUrl: 'reserva.html',
})

export class ReservaPage {

  public servicios: any[];
  public carrito: CarritoOptions[] = [];
  public disponibilidadSeleccionada: ReservaOptions;
  public disponibilidadBloquear: ReservaOptions[] = [];
  public horario: ReservaOptions[];
  public cantidad = 0;
  public ultimoHorario: Date;
  public totalServicios: number = 0;

  public cliente: ClienteOptions = {
    identificacion: null,
    nombre: null,
    telefono: null,
    correoelectronico: null
  };

  constructor(
    public alertCtrl: AlertController,
    public modalCtrl: ModalController,
    private navParams: NavParams,
    public popoverCtrl: PopoverController,
    public viewCtrl: ViewController) {
    this.disponibilidadSeleccionada = this.navParams.get('disponibilidad');
    this.horario = this.navParams.get('horario');
    this.ultimoHorario = this.disponibilidadSeleccionada.fechaInicio;
  }

  ionViewDidLoad() {
    let clienteModal = this.modalCtrl.create('DetallePersonaPage');
    clienteModal.onDidDismiss(data => {
      if (data) {
        this.cliente = data;
      } else {
        this.viewCtrl.dismiss();
      }
    });
    clienteModal.present();
    this.updateServicios();
  }

  updateServicios() {
    let servicios: ServicioOptions[] = [];
    let servicio1: ServicioOptions = { id: 1, nombre: 'Servicio 1', descripcion: 'Esta es la descripci贸n del Servicio 1', duracion_MIN: 40, valor: 1000, activo: true, imagen: 'data:image/jpeg;base64,', grupo: 'Grupo 1' };
    let servicio2: ServicioOptions = { id: 2, nombre: 'Servicio 2', descripcion: 'Esta es la descripci贸n del Servicio 2', duracion_MIN: 60, valor: 2000, activo: true, imagen: 'data:image/jpeg;base64,', grupo: 'Grupo 1' };
    let servicio3: ServicioOptions = { id: 3, nombre: 'Servicio 3', descripcion: 'Esta es la descripci贸n del Servicio 3', duracion_MIN: 80, valor: 3000, activo: true, imagen: 'data:image/jpeg;base64,', grupo: 'Grupo 2' };
    let servicio4: ServicioOptions = { id: 4, nombre: 'Servicio 4', descripcion: 'Esta es la descripci贸n del Servicio 4', duracion_MIN: 100, valor: 800000, activo: true, imagen: 'data:image/jpeg;base64,', grupo: 'Grupo 3' };
    servicios.push(servicio1);
    servicios.push(servicio2);
    servicios.push(servicio3);
    servicios.push(servicio4);

    let grupos = [];
    this.servicios = [];
    servicios.forEach(servicio => {
      let grupo = servicio.grupo;
      if (grupos[grupo] === undefined) {
        grupos[grupo] = [];
      }
      grupos[grupo].push(servicio);
    });

    for (let grupo in grupos) {
      this.servicios.push({ grupo: grupo, servicios: grupos[grupo] });
    }
  }

  genericAlert(titulo: string, mensaje: string) {
    let mensajeAlert = this.alertCtrl.create({
      title: titulo,
      message: mensaje,
      buttons: ['OK']
    });

    mensajeAlert.present();
  }

  agregar(servicio: ServicioOptions) {
    let disponibilidadBloquear: ReservaOptions[] = [];
    let disponibilidadEncontrada: ReservaOptions;
    let disponible: boolean = true;
    let contador = 0;
    for (let i = 0; i <= servicio.duracion_MIN / 10 - 1; i++) {
      contador = i;
      let horaInicio = moment(this.ultimoHorario).add(i * 10, 'minutes').toDate();

      disponibilidadEncontrada = this.horario.filter(disponibilidad =>
        disponibilidad.fechaInicio.getTime() === horaInicio.getTime()
      )[0];

      if (!disponibilidadEncontrada || disponibilidadEncontrada.estado !== 'Disponible') {
        disponible = false;
        break;
      } else {
        disponibilidadEncontrada.servicio = servicio;
        disponibilidadBloquear.push(disponibilidadEncontrada);
      }
    }

    if (disponible) {
      this.disponibilidadBloquear.push.apply(this.disponibilidadBloquear, disponibilidadBloquear);
      this.cantidad++;
      servicio.activo = false;
      this.carrito.push({
        servicio: servicio,
        horaDesde: disponibilidadBloquear[0].fechaInicio,
        horaHasta: disponibilidadBloquear[disponibilidadBloquear.length - 1].fechaFin
      });
      this.ultimoHorario = disponibilidadBloquear[disponibilidadBloquear.length - 1].fechaFin;
      this.totalServicios += servicio.valor;
    } else if (!disponibilidadEncontrada) {
      this.genericAlert('Error al reservar', 'Disponibilidad' + + ' no encontrada');
    } else if (contador === 0) {
      this.genericAlert('Error al reservar', 'La cita se cruza con ' + disponibilidadEncontrada.cliente.nombre + ', la reserva ha sido cancelada');
    } else {
      let fechaInicio = moment(disponibilidadEncontrada.fechaInicio).locale("es").format("dddd, DD [de] MMMM [de] YYYY");
      let horaInicio = moment(disponibilidadEncontrada.fechaInicio).format("hh:mm a");
      let cruceAlert = this.alertCtrl.create({
        title: 'Cruce de cita',
        message: 'La cita ' + fechaInicio + ' a las ' + horaInicio + ' se cruza con la cita de ' + disponibilidadEncontrada.cliente.nombre + '\n 驴Desea continuar con esta reserva?',
        buttons: [
          {
            text: 'NO',
            handler: () => {
              disponibilidadBloquear = [];
              this.genericAlert('Reserva cancelada', 'La reserva con ' + this.cliente.nombre + ' ha sido cancelada');
            }
          },
          {
            text: 'SI',
            handler: () => {
              this.disponibilidadBloquear.push.apply(this.disponibilidadBloquear, disponibilidadBloquear);
              servicio.activo = false;
              this.carrito.push({
                servicio: servicio,
                horaDesde: disponibilidadBloquear[0].fechaInicio,
                horaHasta: disponibilidadBloquear[disponibilidadBloquear.length - 1].fechaFin
              });
              this.cantidad++;
              this.ultimoHorario = disponibilidadBloquear[disponibilidadBloquear.length - 1].fechaFin;
              this.totalServicios += servicio.valor;
            }
          }
        ],
      });
      cruceAlert.present();
    }
  }

  eliminar(servicio: CarritoOptions) {
    this.disponibilidadBloquear = [];
    let item = this.carrito.indexOf(servicio);
    let carrito = this.carrito;
    carrito.splice(item, 1);

    this.carrito = [];
    this.cantidad = 0;
    this.totalServicios = 0;
    this.ultimoHorario = this.disponibilidadSeleccionada.fechaInicio;

    carrito.forEach(servicioAdquirido => {
      this.agregar(servicioAdquirido.servicio);
    });
  }

  guardar() {
    this.disponibilidadBloquear.forEach(bloquear => {
      this.horario.forEach(disponibilidad => {
        if (disponibilidad.fechaInicio.getTime() === bloquear.fechaInicio.getTime()) {
          disponibilidad.cliente = this.cliente;
          disponibilidad.estado = 'Reservado';
          disponibilidad.servicio = bloquear.servicio;
        }
      });
    });

    this.viewCtrl.dismiss({
      horario: this.horario
    });
  }

  ver(event) {
    let carritoOptions = this.popoverCtrl.create('CarritoPage', {
      servicios: this.carrito,
      total: this.totalServicios
    });
    carritoOptions.present({
      ev: event
    });

    carritoOptions.onDidDismiss(data => {
      if (data && data.metodo === 'eliminar') {
        this.eliminar(data.servicio);
      } else if (data && data.metodo === 'guardar') {
        this.guardar();
      }
    });
  }

}
